<template>
  <ModalsContainer title="属性信息" class="webgis-attribute-info">
    <div class="webgis-attribute-info__filter">
      <label>筛选条件</label>
      <el-select
        class="webgis-attribute-info__select"
        :value="filter"
        size="mini"
        placeholder="请选择"
      >
        <el-option
          v-for="item in filterData"
          :key="item.value"
          :label="item.label"
          :value="item.value"
        >
        </el-option>
      </el-select>
    </div>
    <div class="webgis-attribute-info__body" v-if="selectedNode.id">
      <el-row class="webgis-attribute-info__content">
        <el-col class="webgis-attribute-info__tree" :span="6">
          <el-tree
            ref="tree"
            :data="data"
            :props="defaultProps"
            :current-node-key="selectedNode.id"
            node-key="id"
            default-expand-all
            highlight-current
            @node-click="selectItem"
          >
            <template slot-scope="{ node, data }">
              <span class="webgis-attribute-info__tree-node">
                {{ data.name }}
              </span>
            </template>
          </el-tree>
        </el-col>
        <el-col class="webgis-attribute-info__list" :span="18">
          <ul v-if="selectedNode">
            <li
              v-for="(value, key) in selectedNode.properties"
              :key="key"
              class="webgis-attribute-info__list-item"
            >
              <label :title="key" class="webgis-attribute-info__list-label">
                {{ key }}
              </label>
              <span class="webgis-attribute-info__list-text">
                {{ value }}
              </span>
            </li>
          </ul>
        </el-col>
      </el-row>
      <div class="webgis-attribute-info__footer">
        图元总数：
        <span class="webgis-attribute-info__total-number">
          {{ selectedTotal }}
        </span>
      </div>
    </div>
  </ModalsContainer>
</template>

<script>
import ModalsContainer from '../components/modals-container';
import { webgisInject } from '../../../mixins/webgis-init';
import { privateEventHubInject } from '../../../mixins/private-event-hub';
import { checkValidArray } from '../../../utils/check-array';

export default {
  name: 'WebGISModalsAttributeInfo',
  mixins: [webgisInject, privateEventHubInject],
  components: {
    ModalsContainer
  },
  props: {
    data: Array
  },
  data() {
    return {
      filter: 'all',
      filterData: [
        {
          value: 'all',
          label: '全部'
        }
      ],
      defaultProps: {
        children: 'children'
      },
      selectedNode: {}
    };
  },
  computed: {
    selectedTotal() {
      let count = 0;
      this.data.forEach((item) => {
        item.children.forEach(() => {
          count++;
        });
      });
      return count;
    }
  },
  methods: {
    selectItem(data) {
      if (data && data.properties) {
        this.selectedNode = data;
        this.$webgis.modules.featureSelect.selectFeature(data.properties.BSM);
        this.$nextTick(() => this.$refs.tree.setCurrentKey(data.id));
      }
    },
    selectFirst() {
      if (checkValidArray(this.data)) {
        this.selectItem(this.data[0].children[0]);
      }
    },
    featureSelectItem(BSM) {
      this.data.forEach((item) =>
        this.selectItem(
          item.children.find((item) => item.properties.BSM === BSM)
        )
      );
    }
  },
  watch: {
    data() {
      this.selectFirst();
    }
  },
  activated() {
    this.selectFirst();
  },
  deactivated() {
    const featureSelect = this.$webgis.modules.featureSelect;
    featureSelect.deactivate();
    this.$webgis.clearFeature();
  },
  created() {
    this.$eventHub.$on('featureSelectItem', this.featureSelectItem);
  },
  beforeDestroy() {
    this.$eventHub.$off('featureSelectItem', this.featureSelectItem);
  }
};
</script>

