<template>
  <ModalsContainer :title="title" class="modal-radio-toggle">
    <div class="modal-radio-toggle__body">
      <div class="modal-radio-toggle__form-group">
        <h4 v-if="subTitle" class="modal-radio-toggle__label">
          {{ subTitle }}
        </h4>
        <el-radio-group
          v-model="privateValue"
          @change="$emit('change', $event)"
        >
          <el-radio
            v-for="item in options"
            :key="item.label"
            :label="item.label"
          >
            {{ item.name }}
          </el-radio>
        </el-radio-group>
      </div>
    </div>
  </ModalsContainer>
</template>

<script>
import ModalsContainer from '../components/modals-container';

export default {
  name: 'WebGISModalsRadioToggle',
  components: {
    ModalsContainer
  },
  model: {
    props: 'value',
    event: 'change'
  },
  props: {
    title: String,
    subTitle: String,
    value: [String, Number],
    options: Array // example: [{ name, label }]
  },
  data() {
    return {
      privateValue: this.value
    };
  },
  watch: {
    value() {
      this.privateValue = this.value;
    }
  }
};
</script>

