<template>
  <div @click.stop class="webgis-opacity-set">
    <el-slider
      class="webgis-opacity-set__slider"
      v-model="opacity"
      @input="$emit('change', opacity)"
      :min="0"
      :max="100"
    ></el-slider>
  </div>
</template>

<script>
export default {
  props: {
    visible: Boolean,
    defaultValue: {
      type: Number,
      default: 100
    }
  },
  data() {
    return {
      opacity: this.defaultValue
    };
  }
};
</script>

