<template>
  <div class="webgis-aside scrollbar-default">
    <el-tree
      ref="elTree"
      @check="onCheckChange"
      :default-checked-keys="showLayers"
      :default-expanded-keys="expandedKeys"
      :data="treeNodes"
      show-checkbox
      node-key="id"
    >
      <div
        slot-scope="{ node, data }"
        class="webgis-aside__tree-item"
        :class="{ 'is-layer': data.nodeType === 'layer' }"
      >
        <label :title="data.name">{{ data.name }}</label>
        <el-dropdown
          @command="dropdownSelect($event, data)"
          v-if="data.nodeType === 'layer'"
          size="medium"
          class="webgis-aside__tree-tools"
        >
          <span class="webgis-aside__tree-select-toggle">
            <i class="el-icon-setting"></i>
          </span>
          <el-dropdown-menu slot="dropdown">
            <el-dropdown-item command="zoom">
              <i class="el-icon-zoom-in"></i>缩放至
            </el-dropdown-item>
            <el-dropdown-item
              command="opacity"
              class="webgis-aside__tree-select-item"
            >
              <i class="el-icon-brush"></i>透明度
              <AsideOpacitySet
                @change="opacitySet(data, $event)"
                :default-value="data.opacity"
              ></AsideOpacitySet>
            </el-dropdown-item>
            <el-dropdown-item v-if="node.previousSibling" command="move-up">
              <i class="el-icon-top"></i>上移
            </el-dropdown-item>
            <el-dropdown-item v-if="node.nextSibling" command="move-down">
              <i class="el-icon-bottom"></i>下移
            </el-dropdown-item>
          </el-dropdown-menu>
        </el-dropdown>
      </div>
    </el-tree>
  </div>
</template>

<script>
import AsideOpacitySet from './aside-opacity-set';
import { webgisInject } from '../../../mixins/webgis-init';

export default {
  mixins: [webgisInject],
  components: {
    AsideOpacitySet
  },
  data() {
    return {
      treeNodes: [],
      showLayers: [],
      expandedKeys: []
    };
  },
  methods: {
    afterInitWebgis() {
      const webgis = this.$webgis;
      this.treeNodes = webgis.treeNodes;
      this.showLayers = webgis.showLayers;
      this.expandedKeys = [...this.showLayers]; // 仅执行一次
      webgis.eventHub.on('layerChange', this.updateShowLayers);
    },
    onCheckChange(data, { checkedKeys }) {
      const checked = checkedKeys.includes(data.id);
      this.$webgis.checkChange(data.id, checked);
    },
    dropdownSelect(type, data) {
      switch (type) {
        case 'zoom':
          // declaration in case have to block `{}`
          {
            const { center, zoom } = data.configGroup;
            this.$webgis.interactions.zoom.animate({ center, zoom });
          }
          break;
        case 'opacity':
          // none
          break;
        case 'move-up':
          this.$webgis.setLayerSort(data.id, 'up');
          this.treeNodes = [...this.$webgis.treeNodes]; // trigger change
          break;
        case 'move-down':
          this.$webgis.setLayerSort(data.id, 'down');
          this.treeNodes = [...this.$webgis.treeNodes]; // trigger change
          break;
        default:
      }
    },
    opacitySet(data, opacity) {
      data.opacity = opacity;
      this.$webgis.setLayerOpacity(data.id, opacity / 100);
    },
    updateShowLayers(showLayers) {
      this.showLayers = showLayers;
    }
  },
  beforeDestroy() {
    this.$webgis.eventHub.off('layerChange', this.updateShowLayers);
  }
};
</script>

