    &__content {
      height: 36px;
      &:hover {
        .webgis-aside__tree-tools {
          display: block;
        }
        .webgis-aside__tree-item.is-layer {
          padding-right: 28px;
        }
      }
    }
    &__children {
      .el-tree-node__content {
        height: 30px;
      }
    }
  }
  &__tree-item {
    width: 100%;
    line-height: 1;
    padding-right: $spacing-base;
    position: relative;
    overflow: hidden;
    text-overflow: ellipsis;
  }
  &__tree-tools.el-dropdown {
    display: none;
