<template>
  <ul
    :style="{ height }"
    class="webgis-combined-search-list webgis-list-unstyled webgis-scrollbar-default"
  >
    <li
      v-for="item in visibleData"
      :key="item.value"
      :class="{ active: value === item.value }"
      @click="value !== item.value && $emit('change', item.value)"
    >
      {{ item.label }}
    </li>
    <li class="is-empty" v-if="!data.length">（无数据）</li>
  </ul>
</template>

<script>
export default {
  name: 'WebGISCombinedSearchList',
  model: {
    prop: 'value',
    event: 'change'
  },
  props: {
    data: {
      type: Array,
      default() {
        return [];
      }
    },
    keyword: String,
    height: {
      type: String,
      default: '60px'
    },
    value: [String, Number]
  },
  computed: {
    visibleData() {
      const keyword = this.keyword;
      if (keyword) {
        return this.data.filter((item) => item.label.includes(keyword));
      } else {
        return this.data;
      }
    }
  }
};
</script>

<style lang="scss" scoped>
@import '../../../../styles/common-variables';

.webgis-combined-search-list {
  border: $border-base;
  border-radius: $border-radius-base;
  padding: 4px;
  overflow-y: auto;
  > li {
    user-select: none;
    height: 16px;
    line-height: 16px;
    padding: 0 4px;
    border-radius: inherit;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    &:hover {
      background-color: rgba(0, 0, 0, 0.05);
    }
    &.active {
      background-color: rgba(0, 0, 0, 0.1);
    }
    &.is-empty {
      color: $color-text-secondary;
      &:hover {
        background-color: inherit;
      }
    }
  }
}
</style>
