<template>
  <div
    class="webgis-rolling-screen"
    :class="{ 'webgis-rolling-screen_horizontal': type === 'horizontal' }"
  >
    <div
      :style="lineStyle"
      class="webgis-rolling-screen__line webgis-absolute-layer"
    ></div>
    <!-- vertical：与地图的裁剪布局相反，所以取相反值 -->
    <el-slider
      class="webgis-rolling-screen__slider webgis-absolute-layer"
      v-model="privateValue"
      @input="$emit('input', privateValue)"
      :vertical="type === 'horizontal'"
      :show-tooltip="false"
    >
    </el-slider>
  </div>
</template>

<script>
export default {
  name: 'WebGISRollingScreen',
  props: {
    type: String, // vertical/horizontal
    value: Number
  },
  data() {
    return {
      privateValue: this.value
    };
  },
  computed: {
    lineStyle() {
      const value = this.privateValue + '%';
      return this.type === 'vertical' ? { left: value } : { bottom: value };
    }
  },
  watch: {
    value() {
      this.privateValue = this.value;
    }
  }
};
</script>

<style lang="scss">
// 覆盖 element-ui 样式
.webgis-rolling-screen {
  &__slider {
    .el-slider__runway,
    .el-slider__bar {
      margin: 0;
      height: 0;
      background: none;
    }
  }
}
</style>

<style lang="scss" scoped>
@import '../../../styles/common-variables';

.webgis-rolling-screen {
  &__line {
    height: 100%;
    width: 1px;
    background: $color-primary;
    z-index: 0;
    left: 0;
  }
  &__slider {
    position: absolute !important;
    width: 100%;
    top: 50%;
    left: 0;
    transform: translate(0, -50%);
    z-index: 0;
  }
  &.webgis-rolling-screen_horizontal {
    .webgis-rolling-screen__line {
      height: 1px;
      width: 100%;
    }
    .webgis-rolling-screen__slider {
      width: auto;
      height: 100%;
      top: 0;
      left: 50%;
      transform: translate(-50%, 0);
    }
  }
}
</style>
