import request from '../../utils/request';
import { SERVER_CONFIG } from '../../config/server';

/**
 * 相交查询
 * @returns {Promise}
 */
export function getIntersectGeometry(sr, geometries, geometry) {
  return request({
    url:
      SERVER_CONFIG.arcgisRestService +
      'Utilities/Geometry/GeometryServer/intersect',
    method: 'post',
    data: {
      f: 'json',
      sr,
      geometry,
      geometries
    },
    analysisToken: true
  });
}
