import { mergeConfig } from '../../utils/update-config';

// 默认的行政区代码（例如：620100，用于地图初始化展示），图层树中排序在最后
export const defaultVisibleService = {
  land: '',
  region: '', // 数组类型第一位在上面，最后一位在下面
  special: '',
  year: '',
  scale: '',
  layer: ''
};

// 默认图层配置
export const defaultLayerConfig = {
  // wmts/wms 图层类型的临界值
  // 1-3 展示 wmts，3- 展示 wms
  zoomThreshold: 0
};

// 设置默认的图层配置
export function setDefaultLayerConfig(config) {
  mergeConfig(defaultLayerConfig, config);
}

// 设置默认展示的服务
export function setDefaultVisibleService(config) {
  mergeConfig(defaultVisibleService, config);
}
