/**
 * 切换激活状态
 */
export default class ToggleActive {
  isActivated = false;

  constructor() {}

  /**
   * 切换状态
   */
  toggleActive(state = !this.isActivated, ...params) {
    if (state) {
      this.activate(...params);
    } else {
      this.deactivate();
    }
  }

  // extends
  activateHandle() {}

  // extends
  deactivateHandle() {}

  // extends
  activate(...params) {
    // deactivate old
    this.isActivated && this.deactivate();
    this.isActivated = true;

    this.activateHandle(...params);
  }

  // extends
  deactivate() {
    // return if not activated
    if (!this.isActivated) return;
    this.isActivated = false;

    this.deactivateHandle();
  }
}
