import OlDragPan from 'ol/interaction/DragPan';
import ToggleActive from '../common/toggle-active';

export default class DragPan extends ToggleActive {
  _olMap;
  _dragPan = new OlDragPan();

  /**
   * @param {Object<Map>} olMap
   */
  constructor({ olMap }) {
    super();

    this._olMap = olMap;
    this.toggleActive();
  }

  // 激活 extends ToggleActive
  activateHandle() {
    this._olMap.addInteraction(this._dragPan);
  }

  // 关闭 extends ToggleActive
  deactivateHandle() {
    this._olMap.removeInteraction(this._dragPan);
  }
}
