/**
 * 格式化长度单位
 */
export function formatLengthToStr(length) {
  let output;
  if (length > 999) {
    output = Math.round((length / 1000) * 100) / 100 + ' ' + 'km';
  } else {
    output = Math.round(length * 100) / 100 + ' ' + 'm';
  }
  return output;
}

/**
 * 格式化面积单位
 */
export function formatAreaToString(area) {
  let output;
  if (area > 99999) {
    output = Math.round((area / 1000000) * 100) / 100 + ' ' + 'k㎡';
  } else {
    output = Math.round(area * 100) / 100 + ' ' + '㎡';
  }
  return output;
}
