const allowedTypes = ['boolean', 'string', 'number'];

/**
 * 将对象转换为简单格式（去掉对象）
 */
export function formatObjectSimple(obj) {
  const result = {};
  let item;
  for (let key in obj) {
    if (obj.hasOwnProperty(key)) {
      item = obj[key];
      if (allowedTypes.includes(typeof item)) {
        result[key] = item;
      }
    }
  }
  return result;
}
