/**
 * 参数转换为数组，如果是数组，则拷贝一份
 * @param {*} param
 * 1. 'test' => ['test']
 * 2. 'test,test2' => ['test', 'test2']
 * @return {[]}
 */
export default function paramToArray(param) {
  if (!Array.isArray(param)) {
    if (typeof param === 'string' && param.includes(',')) {
      param = param.split(',').map((item) => item.trim());
    } else if (param || param === 0) {
      param = [param];
    } else {
      param = [];
    }
  } else {
    param = [...param];
  }
  return param;
}
