/**
 * 清理地图上的要素与信息框
 * @example
 * ```
 * import controlsClean from './controls-clean';
 *
 * export default {
 *   mixins: [controlsClean], // 加入
 *   methods: {
 *     doSomeThing() {
 *       this.clean(); // 执行清理
 *     }
 *   }
 * }
 * ```
 */
import { privateEventHubInject } from './private-event-hub';
import { webgisInject } from './webgis-init';

export default {
  mixins: [privateEventHubInject, webgisInject],
  methods: {
    clear() {
      this.$eventHub.$emit('clear');
    }
  }
};
