/**
 * 分屏状态切换
 * @example
 * ```
 * import splitScreenToggle from './split-screen-toggle';
 *
 * export default {
 *   mixins: [splitScreenToggle], // 加入
 *   methods: {
 *     doSomeThing() {
 *       this.splitScreenToggle(); // 进行切换
 *     }
 *   }
 * }
 * ```
 */
import eventHub from '../utils/event-hub';

export default {
  methods: {
    splitScreenToggle() {
      eventHub.$emit('splitScreenToggle');
    }
  }
};
