/**
 * 合并配置（深合并）
 * @param {Object} source 源配置
 * @param {Object} config 传入配置
 */
export function mergeConfig(source, config) {
  if (config instanceof Object) {
    let item, sourceItem;
    for (let key in config) {
      if (config.hasOwnProperty(key)) {
        item = config[key];
        sourceItem = source[key];
        if (item || item === 0) {
          if (item instanceof Object) {
            if (!sourceItem) {
              source[key] = item;
            } else {
              mergeConfig(sourceItem, item);
            }
          } else {
            source[key] = item;
          }
        }
      }
    }
  }
}
